<script>
    document.addEventListener('DOMContentLoaded', function () {
  const tabs = document.querySelectorAll('.tab');
  const contentSections = document.querySelectorAll('[id^="tabs-"]');

  tabs.forEach(tab => {
    tab.addEventListener('click', function () {
      // Remove 'active' class and styling from all tabs
      tabs.forEach(t => {
        t.classList.remove('active', 'text-blue-600', 'border-blue-600');
        t.classList.add('text-gray-500', 'hover:text-gray-700', 'border-transparent', 'hover:border-blue-600');
        t.setAttribute('aria-selected', 'false');
      });

      // Hide all content sections
      contentSections.forEach(content => {
        content.classList.add('hidden');
        content.classList.remove('block');
      });

      // Add 'active' class and styling to the clicked tab
      this.classList.add('active', 'text-blue-600', 'border-blue-600');
      this.classList.remove('text-gray-500', 'hover:text-gray-700', 'border-transparent', 'hover:border-blue-600');
      this.setAttribute('aria-selected', 'true');

      // Show the corresponding content section
      const targetId = this.getAttribute('data-target');
      const targetContent = document.getElementById(targetId);
      if (targetContent) {
        targetContent.classList.remove('hidden');
        targetContent.classList.add('block');
      }
    });
  });
});
</script>


